tinymce.PluginManager.add('filemanager', function (editor) {
    function openmanager() {
        var win, data, dom = editor.dom,
            imgElm = editor.selection.getNode();
        var width, height, imageListCtrl;
        var params = tinymce.settings.params;
        var queryString;
        if (params) {
            queryString = "";
            for (var key in params) {
                queryString = queryString + key + "=" + params[key] + "&";
            }
            queryString = queryString.substring(0, queryString.length - 1);
        }
        win = editor.windowManager.open({
            title: '文件管理器',
            file: tinymce.settings.contextPath + '/fileManager?editor='+ editor.id + (queryString ? '&' + queryString : ''),
            filetype: 'all',
            classes: 'filemanager',
            width: 900,
            height: 600,
            inline: 1
        })
    }

    editor.addButton('filemanager', {
        icon: 'browse',
        tooltip: '插入文件',
        onclick: openmanager,
        stateSelector: 'img:not([data-mce-object])'
    });
    editor.addMenuItem('filemanager', {
        icon: 'browse',
        text: '插入文件',
        onclick: openmanager,
        context: 'insert',
        prependToContext: true
    })
});